import 'package:blue_bank_flutter/app/view/decorations/colors.dart';
import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:flutter/material.dart';



class StoreItensList extends StatelessWidget {

  final lista = [
    {'nome_produto' : 'GooglePlay GifCard', 'valor_compra' : 10, 'iconLink' : 'data:image/png;base64,'},
    {'nome_produto' : 'GooglePlay GifCard', 'valor_compra' : 20, 'iconLink' : 'data:image/png;base64,'},
    {'nome_produto' : 'GooglePlay GifCard', 'valor_compra' : 50, 'iconLink' : 'data:image/png;base64,'},
    {'nome_produto' : 'GooglePlay GifCard', 'valor_compra' : 100, 'iconLink' : 'data:image/png;base64,'},
    {'nome_produto' : 'PlayStation GifCard', 'valor_compra' : 50, 'iconLink' : 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/91/PlayStation_App_Icon.jpg/640px-PlayStation_App_Icon.jpg'},
    {'nome_produto' : 'PlayStation GifCard', 'valor_compra' : 100, 'iconLink' : 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/91/PlayStation_App_Icon.jpg/640px-PlayStation_App_Icon.jpg'},
    {'nome_produto' : 'Xbox GifCard', 'valor_compra' : 50, 'iconLink' : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQg5D0y0uMYxr_kF3b6h3Wifr9xZHv0pODLuIW6cgIlQ&s'}, 
    {'nome_produto' : 'Xbox GifCard', 'valor_compra' : 50, 'iconLink' : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSQg5D0y0uMYxr_kF3b6h3Wifr9xZHv0pODLuIW6cgIlQ&s'}, 
  ];
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: backgroundColor,
      body: ListView.builder(
            itemCount: lista.length,
            itemBuilder: (context, i) { 
              var iten = lista[i];
              var icon = CircleAvatar(backgroundImage: NetworkImage(iten['iconLink'].toString()),);
              return Container(
                margin: EdgeInsets.only(left: 500, right: 500, bottom: 20),
                child: Column(
                  children: [
                    Container(
                      decoration: BoxDecoration(
                        color: contrastColor,
                        borderRadius: BorderRadius.circular(15),
                      ),
                      child: ListTile(
                            leading: icon,
                            title: Text(iten['nome_produto'].toString(), style: TextStyle(color: highlightsColor, fontSize: 18),),
                            subtitle: Text(iten['valor_compra'].toString(), style: TextStyle(color: highlightsColor, fontSize: 18)),
                            ),
                      ),
                      SizedBox(height: 12),
                      Row(
                        children: [
                          SizedBox(width: 20),
                          ElevatedButton(
                            style: ElevatedButton.styleFrom(backgroundColor: secondaryBlue),
                            onPressed: () => _registraCompra(iten[i]), 
                            child: Text("Comprar", style: TextStyle(color: highlightsColor, fontSize: 18))),
                        ],
                      ),
                  ],
                ),
                );
             },
          ),
      );
  }

  _registraCompra(var iten) {
    FirebaseFirestore.instance.collection('comprascartão').doc().set({
    'nome_produto' : iten['nome_produto'],
    'valor_compra' : iten['valor_compra'],
    'data_compra' : DateTime.now(),
    'icon_link' : iten['iconLink'],
    });
  }
}